--INIT user
DECLARE @userid NCHAR(20)
SELECT  @userid=cuserid
FROM    sm_user
WHERE   creationtime = ( SELECT MIN(creationtime)
                         FROM   sm_user
                         WHERE  user_type = 0
                       )
					   
--HRORG_EX
DECLARE @defcount INT
SELECT  @defcount=COUNT(pk_infoset_item)
FROM    hr_infoset_item
WHERE   pk_infoset = ( SELECT   pk_infoset
                       FROM     hr_infoset
                       WHERE    infoset_code = N'hrhrorg'
                     )
        AND item_code LIKE N'glbdef%'

DECLARE @poscount INT
SELECT  @poscount=MAX(showorder)
FROM    hr_infoset_item 
WHERE   pk_infoset = ( SELECT   pk_infoset
                       FROM     hr_infoset
                       WHERE    infoset_code = N'hrhrorg'
                     )

