create table wa_deduction (
   pk_deduction     nvarchar(20)         not null,
   pk_psndoc            nvarchar(36)         not null,
   type                 nchar(1)             not null,
   apply_date           nchar(10)            not null,
   begin_year           nchar(4)             not null,
   begin_period         nchar(2)             not null,
   end_year             nchar(4)             null,
   end_period           nchar(2)             null,
   circle_period        nchar(1)             not null,
   amount               int                  not null,
   pk_source            nvarchar(50)         null,
   source            nvarchar(20)         null,
   op_type              nchar(1)             null,
   idcard                 nvarchar(50)         null,
   def1                 nvarchar(50)         null,
   def2                 nvarchar(50)         null,
   def3                 nvarchar(50)         null,
   def4                 nvarchar(50)         null,
   def5                 nvarchar(50)         null,
   constraint pk_wa_deduction primary key nonclustered (pk_deduction),
   ts char(19) null default convert(char(19),getdate(),20),
   dr smallint null default 0,
   creator      	nchar(20) NULL,
	creationtime 	char(19) null default convert(char(19),getdate(),20),
	modifier     	nchar(20) NULL,
	modifiedtime 	nchar(19) NULL 
)
;

