CREATE TABLE
    wa_expformat_head
    (
        pk_formathead CHAR(20) NOT NULL,
        code VARCHAR2(50),
        name VARCHAR2(50),
        linecount INT,
        ts CHAR(19) DEFAULT TO_CHAR(SYSDATE,'yyyy-mm-dd hh24:mi:ss'),
        dr NUMBER(10) DEFAULT 0,
        CONSTRAINT PK_wa_expformat_head PRIMARY KEY (pk_formathead)
    );

CREATE TABLE
    wa_expformat_item
    (
        pk_formathead CHAR(20),
        pk_formatitem CHAR(20) NOT NULL,
        linenumber INT,
        posnumber INT,
        itemcode VARCHAR2(50),
        itemname VARCHAR2(50),
        datatype INT,
        byteLength INT,
        fillmode INT,
        fillstr VARCHAR2(50),
        prefix VARCHAR2(50),
        suffix VARCHAR2(50),
        splitter VARCHAR2(50),
        issum CHAR(1),
        datasource INT,
        datatable VARCHAR2(200),
        joinkey VARCHAR2(1000),
        datacontext VARCHAR2(1000),
        ts CHAR(19) DEFAULT TO_CHAR(SYSDATE,'yyyy-mm-dd hh24:mi:ss'),
       dr NUMBER(10) DEFAULT 0,
        CONSTRAINT PK_wa_expformat_item PRIMARY KEY (pk_formatitem)
    );

